import React, { useState } from 'react';
import TaskForm from './TaskForm.jsx';
import TaskList from './TaskList.jsx';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState('all');

  const addTask = (text, priority, dueDate) => {
    const newTask = { id: Date.now(), text, isComplete: false, priority, dueDate };
    setTasks([...tasks, newTask]);
  };

  const updateTask = (id, updatedText) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, text: updatedText } : task));
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const toggleComplete = (id) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, isComplete: !task.isComplete } : task));
  };

  const clearCompletedTasks = () => {
    setTasks(tasks.filter(task => !task.isComplete));
  };

  // New function to mark all tasks as completed
  const markAllAsCompleted = () => {
    setTasks(tasks.map(task => ({ ...task, isComplete: true })));
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'all') return true;
    if (filter === 'completed') return task.isComplete;
    if (filter === 'incomplete') return !task.isComplete;
    if (filter === 'high-priority') return task.priority === 'High';
    return true;
  });

  return (
    <div className="task-tracker">
      <h1>Task Tracker</h1>
      <TaskForm addTask={addTask} />
      <div className="filter-buttons">
        <button onClick={() => setFilter('all')}>All</button>
        <button onClick={() => setFilter('completed')}>Completed</button>
        <button onClick={() => setFilter('incomplete')}>Incomplete</button>
        <button onClick={() => setFilter('high-priority')}>High Priority</button>
        <button onClick={clearCompletedTasks}>Clear Completed</button>
        {/* New button to mark all tasks as completed */}
        <button onClick={markAllAsCompleted}>Mark All as Completed</button>
      </div>
      <TaskList 
        tasks={filteredTasks} 
        updateTask={updateTask} 
        deleteTask={deleteTask} 
        toggleComplete={toggleComplete} 
      />
    </div>
  );
}

export default App;
