import React, { useState } from 'react';
import TaskForm from './TaskForm';
import TaskList from './TaskList';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);

  const addTask = (text) => {
    const newTask = { id: Date.now(), text, isComplete: false };
    setTasks([...tasks, newTask]);
  };

  const updateTask = (id, updatedText) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, text: updatedText } : task));
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const toggleComplete = (id) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, isComplete: !task.isComplete } : task));
  };

  return (
    <div className="task-tracker">
      <h1>Task Tracker</h1>
      <TaskForm addTask={addTask} />
      <TaskList 
        tasks={tasks} 
        updateTask={updateTask} 
        deleteTask={deleteTask} 
        toggleComplete={toggleComplete} 
      />
    </div>
  );
}

export default App;
